con:config.script:
import groovy.json.JsonSlurper

def response = testRunner.testCase.getTestStepByName("AccessToken").getPropertyValue("response")

def token = new JsonSlurper().parseText(response)

def tokenid =  token.data.access_token

log.info "access token is : $tokenid"

context.testCase.setPropertyValue("accesstoken","bearer " +tokenid)

def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def httpResponseHeaders = context.testCase.testSteps["AccessToken"].testRequest.response.responseHeaders
def httpStatus = httpResponseHeaders["#status#"]
def httpStatusCode = (httpStatus =~ "[1-5]\\d\\d")[0]

log.info("HTTP status code: " + httpStatusCode)

 if(httpStatusCode=='200')
    {
        log.info("Access Token validated");
    }
    else
    {
        log.info "Something is wrong"
    }

